,p_plug_template=>wwv_flow_api.id(5993045016619236)
,p_plug_display_sequence=>10
,p_plug_display_point=>'BODY'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH CHILD_JOBS_W_CCC as (',
'    /*Looks for Child jobs that have an Issued CCC status*/',
'',
'   select PAR_JOB_ID, JOB_ID, COUNT(penta.pk_udfdata.f_value(J.udf_row_id, ''CCC_STATUS'')) CCC_ISSUED, U.CREATION_DATE CCC_DATE',
'    from penta.job j',
'    INNER JOIN PENTA.UDF_DATA U on J.UDF_ROW_ID = U.UDF_ROW_ID AND UDF_COLUMN_NAME = ''CCC_STATUS''',
'    where ',
'        penta.pk_udfdata.f_value(j.udf_row_id, ''CCC_STATUS'') = 1',
'        AND ',
'        LENGTH(J.PAR_JOB_ID) = 6',
'        GROUP BY PAR_JOB_ID, JOB_ID,j.udf_row_id,U.CREATION_DATE),',
'',
'/*CHILD_JOBS_W_REC_CCC  as  (  ',
' ',
'    ',
'    select PAR_JOB_ID, JOB_ID, COUNT(penta.pk_udfdata.f_value(J.udf_row_id, ''CCC_STATUS'')) CCC_ISSUED, U.CREATION_DATE CCC_DATE',
'    from penta.job j',
'    INNER JOIN PENTA.UDF_DATA U on J.UDF_ROW_ID = U.UDF_ROW_ID AND UDF_COLUMN_NAME = ''CCC_STATUS''',
'    where ',
'        penta.pk_udfdata.f_value(j.udf_row_id, ''CCC_STATUS'') = 2',
'        AND ',
'        LENGTH(J.PAR_JOB_ID) = 6',
'        GROUP BY PAR_JOB_ID, JOB_ID,j.udf_row_id,U.CREATION_DATE),*/',
'    ',
'STATUS_DESCR as (SELECT ALPHA_VALUE, VALUE_CD FROM PENTA.UDF_VALUES U WHERE UDF_COLUMN_NAME = ''CCC_STATUS'')',
'',
'--CHILD_JOBS AS (SELECT JOB_ID, PAR_JOB_ID, D.ALPHA_VALUE CCC_STATUS FROM PENTA.JOB_UDF J LEFT OUTER JOIN STATUS_DESCR D on D.VALUE_CD = J.CCC_STATUS WHERE PAR_JOB_ID is not null and LENGTH(PAR_JOB_ID) =6)',
'    ',
'select distinct',
'JD.PMGR_EMP_NAME',
', DECODE(LENGTH(J.PAR_JOB_ID), 6, J.PAR_JOB_ID, '''') PAR_JOB_ID',
', J.JOB_ID',
', J.NAME',
', PENTA.pk_jobRevenue.f_contract(J.JOB_ID, 15, SYSDATE, SYSDATE) M_NUMBER_VALUE',
', d.alpha_value CCC_STATUS ',
', DECODE(J.CCC_STATUS, 1,J.CCC_STATUS_ED, NULL) CCC_STATUS_ED',
', DECODE(J.CCC_STATUS, 1,round(SYSDATE - J.CCC_STATUS_ED), NULL) DAYS_OUTSTANDING',
', j.traffic_and_sl_contact COE_CONTACT',
', PENTA.pk_jobRevenue.f_contract(J.JOB_ID, 15, SYSDATE, SYSDATE) * 0.1 HOLDBACK_VALUE',
', DECODE(J.CCC_STATUS, 1,round(J.CCC_STATUS_ED+30), NULL) CCC_30_REQ_DATE',
', DECODE(J.CCC_STATUS, 1,round(J.CCC_STATUS_ED+60), NULL) CCC_60_REQ_DATE',
', DECODE(J.CCC_STATUS, 1, round(J.CCC_STATUS_ED)+45,NULL) CCC_45_REQ_DATE',
'FROM PENTA.JOB_UDF J',
'INNER JOIN PENTA.JOB_DESCR JD ON J.JOB_ID = JD.JOB_ID',
'LEFT OUTER JOIN STATUS_DESCR D on D.VALUE_CD = J.CCC_STATUS',
'--LEFT OUTER JOIN CHILD_JOBS_W_CCC C on C.PAR_JOB_ID = J.JOB_ID',
'--LEFT OUTER JOIN CHILD_JOBS_W_REC_CCC RC on RC.PAR_JOB_ID = J.JOB_ID',
'--LEFT OUTER JOIN CHILD_JOBS CJ on CJ.PAR_JOB_ID = J.JOB_ID',
'WHERE LENGTH(J.JOB_ID) = 6 -- AND J.PAR_JOB_ID = ''191645''',
'AND ',
' J.CCC_STATUS = 1 OR J.JOB_ID IN (SELECT JOB_ID FROM CHILD_JOBS_W_CCC) or J.PAR_JOB_ID IN (SELECT PAR_JOB_ID FROM CHILD_JOBS_W_CCC)',
'ORDER BY JD.PMGR_EMP_NAME, DECODE(LENGTH(J.PAR_JOB_ID), 6, J.PAR_JOB_ID, ''''), J.JOB_ID, DECODE(J.CCC_STATUS, 1,round(SYSDATE - J.CCC_STATUS_ED),NULL)  DESC',
'',
'/*',
'select distinct',
'J.JOB_ID',
', J.NAME',
', J.DESCR_2',
', listagg(CJ.JOB_ID || CASE WHEN CJ.JOB_ID IS NOT NULL THEN '' ( '' || CJ.CCC_STATUS || '' )'' END, '', '' || CHR(13) || CHR(10)) ',
'    WITHIN GROUP (ORDER BY CJ.PAR_JOB_ID) CHILD_JOBS',
', PENTA.pk_jobRevenue.f_contract(J.JOB_ID, 15, SYSDATE, SYSDATE) M_NUMBER_VALUE',
', NVL(d.alpha_value, case when (select sum(ccc_issued) from child_jobs_w_ccc c where c.PAR_JOB_ID = J.JOB_ID) = (select count(*) from PENTA.JOB c where c.PAR_JOB_ID = J.JOB_ID) THEN ''Issued'' ELSE ''Partial'' END ) CCC_STATUS ',
', NVL(J.CCC_STATUS_ED, C.CCC_DATE) CCC_STATUS_ED',
', round(SYSDATE - NVL(J.CCC_STATUS_ED, MAX(C.CCC_DATE))) DAYS_OUTSTANDING',
', j.traffic_and_sl_contact COE_CONTACT',
', PENTA.pk_jobRevenue.f_contract(J.JOB_ID, 15, SYSDATE, SYSDATE) * 0.1 HOLDBACK_VALUE',
', round(NVL(J.CCC_STATUS_ED, C.CCC_DATE)+30) CCC_30_REQ_DATE',
', to_date(max(J.DATE_RECEIVED_30_DAY_CCC_LE), ''YYYY-MM-DD'') DATE_RECEIVED_30_DAY_CCC_LE',
', round(NVL(J.CCC_STATUS_ED, C.CCC_DATE)+60) CCC_60_REQ_DATE',
', max(RC.CCC_DATE) DATE_REC_PARTIAL_CCC_LTR',
', round(C.CCC_DATE)+45 CCC_45_REQ_DATE',
'FROM PENTA.JOB_UDF J ',
'LEFT OUTER JOIN STATUS_DESCR D on D.VALUE_CD = J.CCC_STATUS',
'LEFT OUTER JOIN CHILD_JOBS_W_CCC C on C.PAR_JOB_ID = J.JOB_ID',
'LEFT OUTER JOIN CHILD_JOBS_W_REC_CCC RC on RC.PAR_JOB_ID = J.JOB_ID',
'LEFT OUTER JOIN CHILD_JOBS CJ on CJ.PAR_JOB_ID = J.JOB_ID',
'WHERE LENGTH(J.JOB_ID) = 6 ',
'AND ',
'(J.JOB_ID IN (SELECT PAR_JOB_ID FROM CHILD_JOBS_W_CCC)  ',
'OR',
' J.CCC_STATUS = 1 AND J.JOB_ID NOT IN (SELECT JOB_ID FROM CHILD_JOBS_W_CCC))',
'GROUP BY',
' J.JOB_ID',
',J.NAME',
',J.DESCR_2',
',c.JOB_ID',
',J.CCC_STATUS',
',c.CCC_ISSUED',
',c.CCC_DATE',
',J.CCC_STATUS_ED',
',j.traffic_and_sl_contact',
',d.alpha_value',
'ORDER BY  round(SYSDATE - NVL(J.CCC_STATUS_ED, MAX(C.CCC_DATE)))  DESC;*/',
''))
,p_plug_source_type=>'NATIVE_IR'
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
