'from PENTA.UDF_DATA_HIST H ',
'WHERE H.UDF_TABLE_NAME = ''JOB'' ',
'AND H.UDF_COLUMN_NAME = ''CCC_STATUS'' ',
'AND H.ALPHA_VALUE = ''1'' --i.e. ''Issued''',
'AND H.D_EFF_DATE >= :P25_ISSUED_DATE',
'GROUP BY H.UDF_ROW_ID',
')',
'select ',
'D.PMGR_EMP_NAME',
', DECODE (LENGTH(PAR_JOB_ID), 6, PAR_JOB_ID, NULL) PAR_JOB_ID ',
', J.JOB_ID',
', J.NAME ',
', H.D_EFF_DATE CCC_ISSUE_DATE',
', (H.D_EFF_DATE + INTERVAL ''2'' YEAR - 30) TWO_YR_WTY_NOTICE',
', (H.D_EFF_DATE + INTERVAL ''2'' YEAR) RECV_FAC_CONFIRM',
', J.FAC_STATUS',
'from penta.job_udf J  ',
'INNER JOIN ',
'    PENTA.JOB_DESCR D on J.JOB_ID = D.JOB_ID',
'INNER JOIN',
'    CCC_ISSUE_DTL H on H.UDF_ROW_ID = J.UDF_ROW_ID',
'where J.CCC_STATUS = 2',
'ORDER BY JOB_ID DESC;'))
,p_plug_source_type=>'NATIVE_IR'
,p_ajax_items_to_submit=>'P25_ISSUED_DATE'
