 p_id=>wwv_flow_api.id(1905697052349852)
,p_plug_name=>'AR Report'
,p_region_template_options=>'#DEFAULT#'
,p_plug_template=>wwv_flow_api.id(5993045016619236)
,p_plug_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'Y'
,p_plug_display_point=>'BODY'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'      "AR_STREAM" || ''-''|| to_char(d_invoice_Date, ''YYYY'') || ''.'' || to_char ( d_INVOICE_DATE,''MM'') "CATEGORY"',
'     ,"GROUPING"',
'     , OU_ID ',
'     , JT_CD',
'     , CUSTOMER_ID',
'     , NAME',
'     , JOB_ID',
'     , JOB_DESCR',
'     , CUS_INVOICE_NUM',
'     , D_INVOICE_DATE',
'     , SUM(DAYS_SINCE_INVOICE) DAYS_SINCE_INVOICE',
'     , CREDIT_LIMIT',
'     , SUM (Amount)   "TOTAL"   ',
'     , SUM (CASE WHEN trunc(sysdate) - trunc(Aging_Date) BETWEEN 0 AND 30 THEN Amount ELSE 0 END) "CURRENT"',
'     , SUM (CASE WHEN trunc(sysdate) - trunc(Aging_Date) BETWEEN 31 AND 60 THEN Amount ELSE 0 END) "31-60"',
'     , SUM (CASE WHEN trunc(sysdate) - trunc(Aging_Date) BETWEEN 61 AND 90 THEN Amount ELSE 0 END) "61-90"',
'     , SUM (CASE WHEN trunc(sysdate) - trunc(Aging_Date) > 90 THEN Amount ELSE 0 END) "OVER 90"',
'     , SUM (Retention_Amount) "RETENTION"',
'     , SUM (Discount_Amount)  "DISCOUNT_AMT"',
'FROM (',
'SELECT c.cus_id "CUSTOMER_ID"',
'     , c.name ',
'     , j.JOB_ID',
'     , j.NAME JOB_DESCR',
'     , ''2'' || j.JT_CD JT_CD',
'     , j.OU_ID',
'     , jt.descr "GROUPING"',
'     , CASE ',
'          WHEN j.jt_cd in (''010'',''011'',''062'',''063'',''022'',''024'',''015'',''023'') THEN ''O&M'' ',
'          WHEN  j.jt_cd in (''058'',''059'',''060'',''052'',''013'',''051'') THEN ''Capital''',
'          ELSE ''SDR'' END "AR_STREAM"',
'      ',
'     ',
'     , abb.CUS_INVOICE_NUM',
'     , abb.d_INVOICE_DATE',
'     , TRUNC(SYSDATE) - TRUNC(abb.D_INVOICE_DATE) "DAYS_SINCE_INVOICE"',
'     , j.CONTACT_FIRST_NAME || '' '' || j.CONTACT_LAST_NAME "Contact Name"',
'     , epc_custom.pkg_cr_utilities.fn_formatCustomerContact(c.cus_id, c.primary_contact_num, NULL, 4) "Contact Phone" -- C_CONTACT_PREF_PHO',
'     , nvl(c.cr_limit_amt,0) "CREDIT_LIMIT"',
'     , CASE WHEN abb.ar_type_cd != ''R'' THEN nvl(abb.amt,0) - nvl(abb.rcvd_amt,0) ELSE 0 END Amount',
'--     , nvl(abb.rcvd_amt,0) Received_Amount',
'     , abb.d_aging_date Aging_date',
'     , nvl(abb.dscnt_amt,0) - nvl(abb.dscnt_taken,0) Discount_Amount',
'     , nvl(abb.pending_amt,0) Pending_Amount',
'      , penta.pk_arDoc.f_openRetention(abb.doc_num, sysdate) Retention_Amount',
'--     , CASE WHEN abb.ar_type_cd = ''R'' THEN nvl(abb.amt,0) - nvl(abb.rcvd_amt,0) ELSE 0 END Retention_Amount',
'FROM  PENTA.JOB j ',
'INNER JOIN penta.ar_bh_base abb ON J.JOB_ID = abb.job_id',
'INNER JOIN penta.customer c on j.cus_id = c.cus_id',
'INNER JOIN penta.JOB_TYPE jt on jt.jt_cd = j.jt_cd',
'  AND J.OU_ID like ''3569_035'' ',
'  AND J.JT_CD in (''010'',''011'',''012'',''058'',''060'',''062'',''053'',''022'',''024'',''029'',''052'',''013'',''015'',''016'',''023'',''051'')',
'  AND to_char(j.creation_Date, ''YYYY'') = ''2019''',
'  AND  abb.ar_stat_cd = ''A''',
'  AND  trunc(abb.d_aging_date) <= trunc(sysdate)',
'',
'union all',
'SELECT c.cus_id "Customer Id"',
'      , c.name     "Name"',
'     , w.wo_id',
'     , w.descr',
'     , w.wo_service_type_CD',
'     , w.OU_ID',
'     , st.descr',
'     , CASE ',
'          WHEN substr(w.wo_service_type_CD,2,3) in (''010'',''011'',''062'',''063'',''022'',''024'',''015'',''023'') THEN ''O&M'' ',
'          WHEN substr(w.wo_service_type_CD,2,3) in (''058'',''059'',''060'',''052'',''013'',''051'') THEN ''Capital''',
'          ELSE ''SDR'' END "AR_STREAM"  ',
'     , abb.CUS_INVOICE_NUM',
'     , abb.d_INVOICE_DATE',
'     , TRUNC(SYSDATE) - TRUNC(abb.D_INVOICE_DATE) "DAYS_SINCE_INVOICE"',
'     , w.INVOICE_CONTACT_FIRST_NAME || '' '' || w.INVOICE_CONTACT_LAST_NAME "Contact Name"  -- C_CONTACT_PREF_NOLCF',
'     , epc_custom.pkg_cr_utilities.fn_formatCustomerContact(c.cus_id, c.primary_contact_num, NULL, 4) "Contact Phone" -- C_CONTACT_PREF_PHO',
'     , nvl(c.cr_limit_amt,0) "Credit Limit"',
'     , CASE WHEN abb.ar_type_cd != ''R'' THEN nvl(abb.amt,0) - nvl(abb.rcvd_amt,0) ELSE 0 END Amount',
'--     , nvl(abb.rcvd_amt,0) Received_Amount',
'     , abb.d_aging_date Aging_date',
'     , nvl(abb.dscnt_amt,0) - nvl(abb.dscnt_taken,0) Discount_Amount',
'     , nvl(abb.pending_amt,0) Pending_Amount',
'     , penta.pk_arDoc.f_openRetention(abb.doc_num, sysdate) Retention_Amount',
'--     , CASE WHEN abb.ar_type_cd = ''R'' THEN nvl(abb.amt,0) - nvl(abb.rcvd_amt,0) ELSE 0 END Retention_Amount',
'FROM  PENTA.WORK_ORDER W ',
'INNER JOIN  penta.ar_bh_base abb ON W.wo_sa_num = abb.wo_sa_num',
'INNER JOIN penta.customer c on w.cus_id = c.cus_id',
'INNER JOIN PENTA.wo_service_type st on st.wo_service_type_cd = w.wo_service_type_cd',
'  AND w.OU_ID like ''3569_035'' ',
'  AND substr(w.wo_service_type_CD,2,3) in (''010'',''011'',''012'',''058'',''060'',''062'',''053'',''022'',''024'',''029'',''052'',''013'',''015'',''016'',''023'',''051'')',
'  AND to_char(w.creation_Date, ''YYYY'') = ''2019''',
'  AND  abb.ar_stat_cd = ''A''',
'  AND  trunc(abb.d_aging_date) <= trunc(sysdate)',
'',
') GROUP BY ',
'      AR_STREAM',
'     , GROUPING',
'     , CUSTOMER_ID',
'     , NAME',
'     , JOB_ID',
'     , JOB_DESCR',
'     , JT_CD',
'     , OU_ID',
'     , CUS_INVOICE_NUM',
'     , D_INVOICE_DATE',
'     , CREDIT_LIMIT   ',
'     ;',
'     ',
'     ',
'     '))
,p_plug_source_type=>'NATIVE_IG'
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
,p_plug_read_only_when_type=>'NEVER'
);
